import React, { Component } from "react";
import vis from "vis";
import { timeline } from "vis-timeline";

class VisChart extends Component {
  constructor(props) {
    super(props);

    this.timeline = null;

    this.showVisibleGroups = this.showVisibleGroups.bind(this);
    this.groupFocus = this.groupFocus.bind(this);

    const now = Date.now();

    const options = {
      stack: true,
      maxHeight: 640,
      horizontalScroll: false,
      verticalScroll: true,
      zoomKey: "ctrlKey",
      start: now - 1000 * 60 * 60 * 24 * 3, // minus 3 days
      end: now + 1000 * 60 * 60 * 24 * 21, // plus 1 months aprox.
      orientation: {
        axis: "both",
        item: "top",
      },
    };

    const groups = new vis.DataSet();
    const items = new vis.DataSet();

    const count = 300;

    for (let i = 0; i < count; i++) {
      const start = now + 1000 * 60 * 60 * 24 * (i + Math.floor(Math.random() * 7));
      const end = start + 1000 * 60 * 60 * 24 * (1 + Math.floor(Math.random() * 5));

      groups.add({
        id: i,
        content: "Task " + i,
        order: i,
      });

      items.add({
        id: i,
        group: i,
        start: start,
        end: end,
        type: "range",
        content: "Item " + i,
      });
    }

    this.state = {
      options: options,
      groups: groups,
      items: items,
    };
  }

  componentDidMount() {
    const container = document.getElementById("visualization");
    this.timeline = new vis.Timeline(container, null, this.state.options);
    this.timeline.setGroups(this.state.groups);
    this.timeline.setItems(this.state.items);

    this.timeline.on("scroll", this.debounce(this.groupFocus, 200));
  }

  showVisibleGroups() {
    const vGroups = this.timeline.getVisibleGroups();
    document.getElementById("visibleGroupsContainer").innerHTML = vGroups.join(", ");
  }

  groupFocus() {
    const vGroups = this.timeline.getVisibleGroups();
    const vItems = vGroups.reduce((res, groupId) => {
      const group = this.timeline.itemSet.groups[groupId];
      if (group.items) {
        res = res.concat(Object.keys(group.items));
      }
      return res;
    }, []);
    this.timeline.focus(vItems);
  }

  debounce(func, wait = 100) {
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func.apply(this, args);
      }, wait);
    };
  }

  render() {
    return (
      <div>
        <h1>Timeline visible Groups</h1>
        <button onClick={showVisibleGroups()}>Show current visible items</button>
        <div>
          <h2>visible groups:</h2>
          <h3 id="visibleGroupsContainer"></h3>
          <h2>
            (Scroll with the mouse and see the items being focus automatically on the
            timeline)
          </h2>
        </div>

        <div id="visualization"></div>
        <br />
      </div>
    );
  }
}

export default VisChart;
