import React, { Component } from "react";
import ReactDOM from 'react-dom';
import ReactApexChart from "react-apexcharts";
import moment from "moment";

import { timeline } from "vis-timeline";
import { DataInterface, DataSet, DataView } from 'vis-data';
import vis from "vis";

class VisChart2 extends Component {
  componentDidMount() {
    this.createTimeline();
  }

  createTimeline() {
    // Your data and options for the timeline
    const container = document.getElementById("visualization");

    const groups = new vis.DataSet([
      { id: 0, content: "Group 1" },
      { id: 1, content: "Group 2" },
    ]);

    const items = new vis.DataSet([
      { id: 0, group: 0, content: "Item 1", start: "2023-01-01" },
      { id: 1, group: 1, content: "Item 2", start: "2023-01-05" },
    ]);

    const options = {
      // Your timeline options
    };

    // Create a Timeline
    const timelineInstance = new timeline.Timeline(container, items, groups, options);

    // Optional: You can store the timeline instance in the component state if needed
    this.setState({ timelineInstance });
  }

  render() {
    return (
      <div>
        <h1>Timeline visible Groups</h1>
        <button onClick={() => this.showVisibleGroups()}>Show current visible items</button>
        <div>
          <h2>visible groups:</h2>
          <h3 id="visibleGroupsContainer"></h3>
          <h2>(Scroll with the mouse and see the items being focused automatically on the timeline)</h2>
        </div>
        <div id="visualization"></div>
        <br />
      </div>
    );
  }

  showVisibleGroups() {
    // Your logic to show visible groups
    const visibleGroups = this.state.timelineInstance.getVisibleGroups();
    document.getElementById("visibleGroupsContainer").innerText = visibleGroups.join(", ");
  }
}

export default VisChart2;
