import React, { Component } from "react";
import ReactDOM from 'react-dom';
import ReactApexChart from "react-apexcharts";
import moment from "moment";

import { timeline } from "vis-timeline";
import { DataInterface, DataSet, DataView } from 'vis-data';
import vis from "vis";


class VisChart extends React.Component {
  constructor(props) {
    super(props);

    function showVisibleGroups() {
      var a = timeline.getVisibleGroups();
      document.getElementById("visibleGroupsContainer").innerHTML = "";
      document.getElementById("visibleGroupsContainer").innerHTML += a;
    }
    
    var now = Date.now();
    
    var options = {
      stack: true,
      maxHeight: 640,
      horizontalScroll: false,
      verticalScroll: true,
      zoomKey: "ctrlKey",
      start: Date.now() - 1000 * 60 * 60 * 24 * 3, // minus 3 days
      end: Date.now() + 1000 * 60 * 60 * 24 * 21, // plus 1 months aprox.
      orientation: {
        axis: "both",
        item: "top",
      },
    };
    var groups = new vis.DataSet();
    var items = new vis.DataSet();
    
    var count = 300;
    
    for (var i = 0; i < count; i++) {
      var start = now + 1000 * 60 * 60 * 24 * (i + Math.floor(Math.random() * 7));
      var end = start + 1000 * 60 * 60 * 24 * (1 + Math.floor(Math.random() * 5));
    
      groups.add({
        id: i,
        content: "Task " + i,
        order: i,
      });
    
      items.add({
        id: i,
        group: i,
        start: start,
        end: end,
        type: "range",
        content: "Item " + i,
      });
    }
    
    // create a Timeline
    var container = document.getElementById("visualization");
    this.timeline = new vis.Timeline(container, null, options);
    this.timeline.setGroups(groups);
    this.timeline.setItems(items);
    
    function debounce(func, wait = 100) {
      let timeout;
      return function(...args) {
        clearTimeout(timeout);
        timeout = setTimeout(() => {
          func.apply(this, args);
        }, wait);
      };
    }
    
    let groupFocus = (e) => {
      let vGroups = this.timeline.getVisibleGroups();
      let vItems = vGroups.reduce((res, groupId) => {
        let group = this.timeline.itemSet.groups[groupId];
        if (group.items) {
          res = res.concat(Object.keys(group.items));
        }
        return res;
      }, []);
      this.timeline.focus(vItems);
    };
    this.timeline.on("scroll", debounce(groupFocus, 200));
  }



  render() {
    return (
      
<div>
  <h1>Timeline visible Groups</h1>

  <button onClick={"showVisibleGroups"}>Show current visible items</button>
  <div>
    <h2>visible groups:</h2>
    <h3 id="visibleGroupsContainer"></h3>
    <h2>
      (Scroll with the mouse and see the items being focus automatically on the
      timeline)
    </h2>
  </div>

  <div id="visualization"></div>
<br />
</div>

      );
    }
  }

  export default VisChart;
